mapper.update:
update blog b
set b.views = b.views + 1
where b.id = #{id}

mapper.select:

select b.id bid, b.title,b.update_time,b.published,b.description,b.type_id,t.id tid,t.name tname
from blog b,type t


        b.type_id = t.id


        and b.type_id = #{typeId}


        and b.title like #{pattern}


order by update_time desc
